                        stroke-width="1"/>
                <!-- Data points -->
                <circle *ngFor="let target of chartData.targets; let i = index"
                       [attr.cx]="(i / (chartData.targets.length - 1)) * 240"
                       [attr.cy]="100 - (target / 8) * 100"
                       r="1.5" 
                       fill="#00d4ff"/>
              </svg>
            </div>
          </div>
          
          <!-- X-axis labels -->
          <div class="x-axis" *ngIf="chartData">
            <span class="x-label" *ngFor="let hour of chartData.hours">{{ hour }}</span>
          </div>
        </div>
      </div>
      
      <div class="statistics-summary">
        <div class="summary-card" *ngIf="chartData">
          <h3>Total Detections</h3>
          <div class="summary-value">{{ chartData.total_detections }}</div>
        </div>
        
        <div class="summary-card" *ngIf="chartData">
          <h3>Peak Hour</h3>
          <div class="summary-value">{{ chartData.peak_hour }}:00</div>
        </div>
        
        <div class="summary-card" *ngIf="chartData">
          <h3>Average/Hour</h3>
          <div class="summary-value">{{ chartData.avg_per_hour }}</div>
        </div>
      </div>
    </div>
  </div>
</div>
