import { Component, OnInit, OnDestroy } from '@angular/core';
import { DataService } from '../../services/data.service';
import { Subscription, interval } from 'rxjs';
import { switchMap } from 'rxjs/operators';

@Component({
  selector: 'app-target-counter',
  templateUrl: './target-counter.component.html',
  styleUrls: ['./target-counter.component.css']
})
export class TargetCounterComponent implements OnInit, OnDestroy {
  targetData: any = null;
  frameIndex: number = 0;
  private dataSubscription?: Subscription;

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    this.dataSubscription = interval(3000).pipe(
      switchMap(() => this.dataService.getTargets(this.frameIndex))
    ).subscribe({
      next: (data) => {
        this.targetData = data;
      },
      error: (error) => {
        console.error('Error fetching target data:', error);
      }
    });
  }

  ngOnDestroy(): void {
    if (this.dataSubscription) {
      this.dataSubscription.unsubscribe();
    }
  }
}
<div class="target-counter-container">
  <div class="targets-info">
    <div class="counter-card">
      <div class="counter-title">Predicted Targets (CNN, frame {{ frameIndex }})</div>
      <div class="counter-value">{{ targetData?.predicted_targets ?? '...' }}</div>
    </div>
  </div>
</div>
