import { Component, OnInit, OnDestroy } from '@angular/core';
import { DataService } from '../../services/data.service';
import { Subscription, interval } from 'rxjs';
import { switchMap } from 'rxjs/operators';

@Component({
  selector: 'app-visualization',
  templateUrl: './visualization.component.html',
  styleUrls: ['./visualization.component.css']
})
export class VisualizationComponent implements OnInit, OnDestroy {
  private dataSubscription?: Subscription;

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    // TODO: implement data fetching and visualization
    this.dataSubscription = interval(5000).pipe(
      switchMap(() => this.dataService.getDoppler())
    ).subscribe({
      next: (data) => {
        console.log('Doppler data:', data);
        // TODO: Update visualization with new data
      },
      error: (error) => {
        console.error('Error fetching Doppler data:', error);
      }
    });
  }

  ngOnDestroy(): void {
    if (this.dataSubscription) {
      this.dataSubscription.unsubscribe();
    }
  }
}
<div class="visualization-container">
  <div class="container">
    <h1>Visualization</h1>
    
    <div class="visualization-grid">
      <div class="visualization-card">
        <h2>Doppler Range</h2>
        <app-doppler-range></app-doppler-range>
      </div>
      
      <div class="visualization-card">
        <h2>Angle Range</h2>
        <app-angle-range></app-angle-range>
      </div>
      
      <div class="visualization-card target-counter-card">
        <h2>Detected Targets</h2>
        <app-target-counter></app-target-counter>
      </div>
    </div>
  </div>
</div>
