import { Component, OnInit } from '@angular/core';
import { DataService } from '../../services/data.service';

interface DopplerData {
  frequencies: number[];
  amplitudes: number[];
  max_amplitude: number;
  peak_frequency: number;
}

@Component({
  selector: 'app-doppler-range',
  templateUrl: './doppler-range.component.html',
  styleUrls: ['./doppler-range.component.css']
})
export class DopplerRangeComponent implements OnInit {
  dopplerData: DopplerData | null = null;
  chartPoints: string = '';
  chartWidth = 300;
  chartHeight = 200;

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    this.fetchDopplerData();
  }

  fetchDopplerData(): void {
    console.log('Fetching Doppler data...');
    this.dataService.getDoppler().subscribe({
      next: (data: DopplerData) => {
        console.log('Doppler data received:', data);
        this.dopplerData = data;
        this.generateChartPoints();
      },
      error: (error) => {
        console.error('Error fetching Doppler data:', error);
      }
    });
  }

  private generateChartPoints(): void {
    if (!this.dopplerData || !this.dopplerData.frequencies || !this.dopplerData.amplitudes) {
      console.error('Invalid doppler data for chart generation');
      return;
    }

    const points: string[] = [];
    const maxAmplitude = this.dopplerData.max_amplitude;
    
    this.dopplerData.frequencies.forEach((freq, index) => {
      const amplitude = this.dopplerData!.amplitudes[index];
      
      // Convert frequency to x position (0 to chartWidth)
      const x = ((freq + 25) / 50) * this.chartWidth; // freq range: -25 to +25
      
      // Convert amplitude to y position (inverted, 0 at top)
      const y = this.chartHeight - (amplitude / maxAmplitude) * this.chartHeight;
      
      points.push(`${x.toFixed(1)},${y.toFixed(1)}`);
    });
    
    this.chartPoints = points.join(' ');
    console.log('Chart points generated:', this.chartPoints.substring(0, 100) + '...');
  }
}

<div class="doppler-range-container">
  <div class="doppler-header">
    <h3>Doppler Frequency Analysis</h3>
    <div class="doppler-info" *ngIf="dopplerData">
      <span>Peak: {{ dopplerData.peak_frequency }}Hz</span>
      <span>Max Amplitude: {{ dopplerData.max_amplitude }}</span>
    </div>
  </div>
  
  <div class="doppler-visualization">
    <div class="chart-container" *ngIf="dopplerData && chartPoints">
      <!-- Y-axis labels -->
      <div class="y-axis">
        <span class="y-label">{{ dopplerData.max_amplitude }}</span>
        <span class="y-label">{{ (dopplerData.max_amplitude * 0.75).toFixed(0) }}</span>
        <span class="y-label">{{ (dopplerData.max_amplitude * 0.5).toFixed(0) }}</span>
        <span class="y-label">{{ (dopplerData.max_amplitude * 0.25).toFixed(0) }}</span>
        <span class="y-label">0</span>
      </div>
      
      <!-- Chart area -->
      <div class="chart-area">
        <!-- Grid lines -->
        <div class="grid-lines">
          <!-- Horizontal grid lines -->
          <div class="grid-line horizontal" style="bottom: 0%"></div>
          <div class="grid-line horizontal" style="bottom: 25%"></div>
          <div class="grid-line horizontal" style="bottom: 50%"></div>
          <div class="grid-line horizontal" style="bottom: 75%"></div>
          <div class="grid-line horizontal" style="bottom: 100%"></div>
          
          <!-- Vertical grid lines -->
          <div class="grid-line vertical" style="left: 0%"></div>
          <div class="grid-line vertical" style="left: 25%"></div>
          <div class="grid-line vertical" style="left: 50%"></div>
          <div class="grid-line vertical" style="left: 75%"></div>
          <div class="grid-line vertical" style="left: 100%"></div>
        </div>
        
        <!-- Data line -->
        <svg class="line-graph" [attr.viewBox]="'0 0 ' + chartWidth + ' ' + chartHeight" preserveAspectRatio="none">
          <polyline [attr.points]="chartPoints" 
                  fill="none" 
                  stroke="#00d4ff" 
                  stroke-width="2"/>
          
          <!-- Peak frequency marker -->
          <circle *ngIf="dopplerData"
                 [attr.cx]="((dopplerData.peak_frequency + 25) / 50) * chartWidth"
                 [attr.cy]="chartHeight - (dopplerData.max_amplitude / dopplerData.max_amplitude) * chartHeight"
                 r="3" 
                 fill="#ff4444"/>
        </svg>
      </div>
      
      <!-- X-axis labels -->
      <div class="x-axis">
        <span class="x-label">-25Hz</span>
        <span class="x-label">-12.5Hz</span>
        <span class="x-label">0Hz</span>
        <span class="x-label">12.5Hz</span>
        <span class="x-label">25Hz</span>
      </div>
    </div>
    
    <div class="loading" *ngIf="!dopplerData">
      Loading Doppler data...
    </div>
  </div>
</div>
