import { Component, OnInit } from '@angular/core';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'app-statistics',
  templateUrl: './statistics.component.html',
  styleUrls: ['./statistics.component.css']
})
export class StatisticsComponent implements OnInit {

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    // TODO: fetch chart data
    this.dataService.getChartData().subscribe({
      next: (data) => {
        console.log('Chart data:', data);
        // TODO: update chart with data
      },
      error: (error) => {
        console.error('Error fetching chart data:', error);
      }
    });
  }
}
<div class="statistics-container">
  <div class="container">
    <h1>Statistics</h1>
    
    <div class="statistics-grid">
      <div class="statistics-card">
        <h2>Target Detection Trends</h2>
        <app-target-chart></app-target-chart>
      </div>
      
      <div class="statistics-summary">
        <div class="summary-card">
          <h3>Daily Average</h3>
          <div class="summary-value">12.5</div>
        </div>
        
        <div class="summary-card">
          <h3>Monthly Total</h3>
          <div class="summary-value">375</div>
        </div>
        
        <div class="summary-card">
          <h3>Detection Rate</h3>
          <div class="summary-value">98.2%</div>
        </div>
      </div>
    </div>
  </div>
</div>
