import { Component, OnInit } from '@angular/core';
import { DataService } from '../../services/data.service';

interface ChartData {
  hours: string[];
  targets: number[];
  total_detections: number;
  peak_hour: number;
  avg_per_hour: number;
}

@Component({
  selector: 'app-statistics',
  templateUrl: './statistics.component.html',
  styleUrls: ['./statistics.component.css']
})
export class StatisticsComponent implements OnInit {
  chartData: ChartData | null = null;
  chartPoints: string = '';

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    this.loadChartData();
  }

  private loadChartData(): void {
    this.dataService.getChartData().subscribe({
      next: (data: ChartData) => {
        console.log('Chart data:', data);
        this.chartData = data;
        this.generateChartPoints();
      },
      error: (error) => {
        console.error('Error fetching chart data:', error);
        // Fallback to default data if API fails
        this.setDefaultData();
      }
    });
  }

  private generateChartPoints(): void {
    if (!this.chartData) return;
    
    const points: string[] = [];
    const maxTargets = 8;
    const chartWidth = 240;
    const chartHeight = 100;
    
    this.chartData.targets.forEach((targets, index) => {
      const x = (index / (this.chartData!.targets.length - 1)) * chartWidth;
      const y = chartHeight - (targets / maxTargets) * chartHeight;
      points.push(`${x},${y}`);
    });
    
    this.chartPoints = points.join(' ');
  }

  private setDefaultData(): void {
    this.chartData = {
      hours: ['00', '02', '04', '06', '08', '10', '12', '14', '16', '18', '20', '22'],
      targets: [2, 3, 4, 6, 5, 4, 3, 2, 1, 2, 3, 2],
      total_detections: 37,
      peak_hour: 6,
      avg_per_hour: 3.1
    };
    this.generateChartPoints();
  }
}
<div class="statistics-container">
  <div class="container">
    <h1>Statistics</h1>
    
    <div class="statistics-grid">
      <div class="statistics-card">
        <h2>Target Detection Trends</h2>
        <div class="line-chart">
          <div class="chart-container">
            <!-- Y-axis labels -->
            <div class="y-axis">
              <span class="y-label">8</span>
              <span class="y-label">7</span>
              <span class="y-label">6</span>
              <span class="y-label">5</span>
              <span class="y-label">4</span>
              <span class="y-label">3</span>
              <span class="y-label">2</span>
              <span class="y-label">1</span>
              <span class="y-label">0</span>
            </div>
            
            <!-- Chart area -->
            <div class="chart-area">
              <!-- Grid lines -->
              <div class="grid-lines">
                <!-- Horizontal grid lines -->
                <div class="grid-line horizontal" style="bottom: 0%"></div>
                <div class="grid-line horizontal" style="bottom: 12.5%"></div>
                <div class="grid-line horizontal" style="bottom: 25%"></div>
                <div class="grid-line horizontal" style="bottom: 37.5%"></div>
                <div class="grid-line horizontal" style="bottom: 50%"></div>
                <div class="grid-line horizontal" style="bottom: 62.5%"></div>
                <div class="grid-line horizontal" style="bottom: 75%"></div>
                <div class="grid-line horizontal" style="bottom: 87.5%"></div>
                <div class="grid-line horizontal" style="bottom: 100%"></div>
                
                <!-- Vertical grid lines -->
                <div class="grid-line vertical" style="left: 0%"></div>
                <div class="grid-line vertical" style="left: 8.33%"></div>
                <div class="grid-line vertical" style="left: 16.66%"></div>
                <div class="grid-line vertical" style="left: 25%"></div>
                <div class="grid-line vertical" style="left: 33.33%"></div>
                <div class="grid-line vertical" style="left: 41.66%"></div>
                <div class="grid-line vertical" style="left: 50%"></div>
                <div class="grid-line vertical" style="left: 58.33%"></div>
                <div class="grid-line vertical" style="left: 66.66%"></div>
                <div class="grid-line vertical" style="left: 75%"></div>
                <div class="grid-line vertical" style="left: 83.33%"></div>
                <div class="grid-line vertical" style="left: 91.66%"></div>
                <div class="grid-line vertical" style="left: 100%"></div>
              </div>
              
              <!-- Data line -->
              <svg class="line-graph" viewBox="0 0 240 100" preserveAspectRatio="none" *ngIf="chartData">
                <polyline [attr.points]="chartPoints" 
                        fill="none" 
                        stroke="#0a2540" 
                        stroke-width="1"/>
                <!-- Data points -->
                <circle *ngFor="let target of chartData.targets; let i = index"
                       [attr.cx]="(i / (chartData.targets.length - 1)) * 240"
                       [attr.cy]="100 - (target / 8) * 100"
                       r="1.5" 
                       fill="#00d4ff"/>
              </svg>
            </div>
          </div>
          
          <!-- X-axis labels -->
          <div class="x-axis" *ngIf="chartData">
            <span class="x-label" *ngFor="let hour of chartData.hours">{{ hour }}</span>
          </div>
        </div>
      </div>
      
      <div class="statistics-summary">
        <div class="summary-card" *ngIf="chartData">
          <h3>Total Detections</h3>
          <div class="summary-value">{{ chartData.total_detections }}</div>
        </div>
        
        <div class="summary-card" *ngIf="chartData">
          <h3>Peak Hour</h3>
          <div class="summary-value">{{ chartData.peak_hour }}:00</div>
        </div>
        
        <div class="summary-card" *ngIf="chartData">
          <h3>Average/Hour</h3>
          <div class="summary-value">{{ chartData.avg_per_hour }}</div>
        </div>
      </div>
    </div>
  </div>
</div>
