import { Component, OnInit } from '@angular/core';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'app-angle-range',
  templateUrl: './angle-range.component.html',
  styleUrls: ['./angle-range.component.css']
})
export class AngleRangeComponent implements OnInit {
  angleData: any = null;

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    this.fetchAngleData();
  }

  fetchAngleData(): void {
    this.dataService.getAngle().subscribe({
      next: (data) => {
        this.angleData = data;
        // TODO: implement animation and visualization of angle data
      },
      error: (error) => {
        console.error('Error fetching angle data:', error);
      }
    });
  }
}
<div class="angle-range-container">
  <div class="angle-visualization">
    <!-- Placeholder for angle visualization -->
    <div class="angle-indicator" *ngIf="angleData">
      <div class="angle-pointer" [style.transform]="'rotate(' + angleData?.angle + 'deg)'"></div>
      <div class="angle-display">{{ angleData?.angle }}°</div>
    </div>
  </div>
  
  <div class="angle-info">
    <div class="range-info">
      <span>Range: {{ angleData?.range[0] }}° to {{ angleData?.range[1] }}°</span>
    </div>
  </div>
</div>
