import { Component, OnInit } from '@angular/core';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'app-doppler-range',
  templateUrl: './doppler-range.component.html',
  styleUrls: ['./doppler-range.component.css']
})
export class DopplerRangeComponent implements OnInit {
  dopplerData: any = null;

  constructor(private dataService: DataService) { }

  ngOnInit(): void {
    this.fetchDopplerData();
  }

  fetchDopplerData(): void {
    this.dataService.getDoppler().subscribe({
      next: (data) => {
        this.dopplerData = data;
        // TODO: implement animation and visualization of Doppler data
      },
      error: (error) => {
        console.error('Error fetching Doppler data:', error);
      }
    });
  }
}

<div class="doppler-range-container">
  <div class="doppler-visualization">
    <!-- Grid background -->
    <div class="grid-background">
      <!-- Horizontal grid lines -->
      <div class="grid-line horizontal" style="bottom: 0%"></div>
      <div class="grid-line horizontal" style="bottom: 20%"></div>
      <div class="grid-line horizontal" style="bottom: 40%"></div>
      <div class="grid-line horizontal" style="bottom: 60%"></div>
      <div class="grid-line horizontal" style="bottom: 80%"></div>
      <div class="grid-line horizontal" style="bottom: 100%"></div>
      
      <!-- Vertical grid lines -->
      <div class="grid-line vertical" style="left: 0%"></div>
      <div class="grid-line vertical" style="left: 20%"></div>
      <div class="grid-line vertical" style="left: 40%"></div>
      <div class="grid-line vertical" style="left: 60%"></div>
      <div class="grid-line vertical" style="left: 80%"></div>
      <div class="grid-line vertical" style="left: 100%"></div>
    </div>
    
    <!-- Doppler bars -->
    <div class="doppler-bar-container">
      <div *ngFor="let value of dopplerData?.values; let i = index" 
           class="doppler-bar"
           [style.height.%]="value"
           [style.animation-delay.ms]="i * 100">
      </div>
    </div>
  </div>
  
  <div class="doppler-info">
    <div class="range-info">
      <span>Range: {{ dopplerData?.range[0] }} - {{ dopplerData?.range[1] }}</span>
    </div>
  </div>
</div>
